using BizzOnBuzzVM1.Models;

using System.Collections.ObjectModel;

namespace BizzOnBuzzVM1;

public partial class Inventory : ContentPage
    
   
{
    private readonly ObservableCollection<Items> _items;

    public Inventory()
	{
     
		InitializeComponent();

		 _items = new ObservableCollection <Items> {
		 new Items {Name ="Kush", Weight = 3.5, Price = 20.00, Description="Sativa", ImageUrl = "data:image/jpeg;base64,"},

         new Items {Name ="Plat", Weight = 3.5, Price = 50.00, Description="Sativa", ImageUrl = "data:image/jpeg;base64,"}
        };
        listView.ItemsSource = _items;
	}

    private void Update_Clicked(object sender, EventArgs e)
    {
      
       
    }

    private void MenuItem_Clicked(object sender, EventArgs e)
    {
       var item = (sender as MenuItem).CommandParameter as Items;
        _items.Remove(item);
    }
}